CREATE TABLE "user"."user" ("id" serial NOT NULL, "image" varchar, "name" varchar NOT NULL, "organisation_id" integer NOT NULL, "username" varchar NOT NULL, "email" varchar, "contact_number" varchar, "password" text NOT NULL, "is_active" bool NOT NULL DEFAULT true, "created_at" timestamptz NOT NULL DEFAULT now(), "updated_at" timestamptz NOT NULL DEFAULT now(), "created_by" integer, "updated_by" integer, PRIMARY KEY ("id") , UNIQUE ("id"), UNIQUE ("username"), UNIQUE ("email"), UNIQUE ("contact_number"));
